def sorted_questions(questions: dict[str, str]) -> dict[str, str]:
  myKeys = list(questions.keys())
  myKeys.sort(key= lambda x: x[3])
  sorted_dict = {i: questions[i] for i in myKeys}
  return sorted_dict
  
